// Usage Upgrade Email Sequence
const opportunity = $input.first().json;
const profile = $input.last().json[0];

const utilization = opportunity.trigger_data?.utilization_percentage || 0;
const currentPlan = opportunity.current_plan;
const recommendedPlan = opportunity.recommended_plan;

// Email sequence based on utilization level
let emailSequence = [];

if (utilization >= 95) {
  // Critical - Immediate action needed
  emailSequence = [
    {
      delay: 0,
      subject: `üö® ${profile.full_name}, you've reached your ${currentPlan} plan limits`,
      template: 'usage_critical_immediate',
      urgency: 'critical'
    },
    {
      delay: 1,
      subject: `Avoid service disruption - Upgrade to ${recommendedPlan} now`,
      template: 'usage_critical_followup',
      urgency: 'critical'
    },
    {
      delay: 3,
      subject: `Last chance: 20% off ${recommendedPlan} upgrade`,
      template: 'usage_critical_discount',
      urgency: 'critical'
    }
  ];
} else if (utilization >= 80) {
  // High - Plan ahead
  emailSequence = [
    {
      delay: 0,
      subject: `${profile.full_name}, you're approaching your ${currentPlan} limits`,
      template: 'usage_high_warning',
      urgency: 'high'
    },
    {
      delay: 3,
      subject: `Unlock more capacity with ${recommendedPlan}`,
      template: 'usage_high_benefits',
      urgency: 'high'
    },
    {
      delay: 7,
      subject: `See how ${recommendedPlan} customers achieve 40% better results`,
      template: 'usage_high_social_proof',
      urgency: 'high'
    }
  ];
}

return emailSequence.map((email, index) => ({
  ...email,
  user_id: opportunity.user_id,
  opportunity_id: opportunity.id,
  email: profile.email,
  full_name: profile.full_name,
  current_plan: currentPlan,
  recommended_plan: recommendedPlan,
  utilization: utilization,
  potential_savings: opportunity.potential_arr_increase,
  sequence_step: index + 1
}));

// Feature Request Email Sequence
const opportunity = $input.first().json;
const profile = $input.last().json[0];

const featureName = opportunity.trigger_data?.feature_name || 'advanced features';
const urgencyLevel = opportunity.trigger_data?.urgency_level || 'medium';
const currentPlan = opportunity.current_plan;
const recommendedPlan = opportunity.recommended_plan;

// Email sequence based on feature request urgency
let emailSequence = [];

if (urgencyLevel === 'critical') {
  emailSequence = [
    {
      delay: 0,
      subject: `${featureName} is available now in ${recommendedPlan}`,
      template: 'feature_critical_immediate',
      urgency: 'critical'
    },
    {
      delay: 1,
      subject: `Urgent: Unlock ${featureName} to unblock your operations`,
      template: 'feature_critical_unblock',
      urgency: 'critical'
    }
  ];
} else if (urgencyLevel === 'high') {
  emailSequence = [
    {
      delay: 0,
      subject: `Good news! ${featureName} is ready for you`,
      template: 'feature_high_available',
      urgency: 'high'
    },
    {
      delay: 2,
      subject: `See ${featureName} in action (live demo)`,
      template: 'feature_high_demo',
      urgency: 'high'
    },
    {
      delay: 5,
      subject: `${recommendedPlan} customers save 40% more time`,
      template: 'feature_high_benefits',
      urgency: 'high'
    }
  ];
} else {
  emailSequence = [
    {
      delay: 0,
      subject: `Your ${featureName} request: Here's what's possible`,
      template: 'feature_medium_overview',
      urgency: 'medium'
    },
    {
      delay: 7,
      subject: `Customer story: How ${featureName} drove 200% growth`,
      template: 'feature_medium_case_study',
      urgency: 'medium'
    },
    {
      delay: 14,
      subject: `Ready to unlock ${featureName}?`,
      template: 'feature_medium_closing',
      urgency: 'medium'
    }
  ];
}

return emailSequence.map((email, index) => ({
  ...email,
  user_id: opportunity.user_id,
  opportunity_id: opportunity.id,
  email: profile.email,
  full_name: profile.full_name,
  feature_name: featureName,
  current_plan: currentPlan,
  recommended_plan: recommendedPlan,
  urgency_level: urgencyLevel,
  potential_value: opportunity.potential_arr_increase,
  sequence_step: index + 1
}));

// Success Milestone Email Sequence
const opportunity = $input.first().json;
const profile = $input.last().json[0];

const milestoneType = opportunity.trigger_data?.milestone_type || 'success achievement';
const milestoneValue = opportunity.trigger_data?.milestone_value || 0;
const currentPlan = opportunity.current_plan;
const recommendedPlan = opportunity.recommended_plan;

// Celebration and progression sequence
const emailSequence = [
  {
    delay: 0,
    subject: `üéâ Congratulations ${profile.full_name}! Amazing ${milestoneType}`,
    template: 'success_celebration',
    urgency: 'high'
  },
  {
    delay: 1,
    subject: `You're ready for the next level: ${recommendedPlan}`,
    template: 'success_progression_ready',
    urgency: 'high'
  },
  {
    delay: 3,
    subject: `Exclusive offer: 25% off ${recommendedPlan} for top performers`,
    template: 'success_exclusive_offer',
    urgency: 'high'
  },
  {
    delay: 7,
    subject: `Join other successful customers in ${recommendedPlan}`,
    template: 'success_peer_validation',
    urgency: 'medium'
  }
];

return emailSequence.map((email, index) => ({
  ...email,
  user_id: opportunity.user_id,
  opportunity_id: opportunity.id,
  email: profile.email,
  full_name: profile.full_name,
  milestone_type: milestoneType,
  milestone_value: milestoneValue,
  current_plan: currentPlan,
  recommended_plan: recommendedPlan,
  achievement_level: 'top_performer',
  sequence_step: index + 1
}));
